const skipped = (ctx, value) => ctx.p0.skip || ctx.p1.skip ? value : undefined;
const down = (ctx, value) => ctx.p0.parsed.y > ctx.p1.parsed.y ? value : undefined;

let graph;
let powerButton;
let turboSwitch;
let humidityTriggerSwitch;
let showSettingsButton;
let saveSettingsButton;
let ventHumidityLevelInput, turboHumidityLevelInput, offHumidityLevelInput, switchDelayInput, buttonDelayInput;
let slider;
let chart;
let chartData;
let divider;

    function getSettings() {
    fetch(`/settings`)
        .then(response => response.json())
        .then(data => {
            updateSettings(data);
            console.log('Налаштування отримані: ' + data);
            saveSettingsButton.disabled = true;
        })
        .catch(error => showError(error));
}

function sendSettings() {
    fetch(`/settings?ventHumidityLevel=${ventHumidityLevelInput.value}&turboHumidityLevel=${turboHumidityLevelInput.value}&offHumidityLevel=${offHumidityLevelInput.value}&switchDelay=${switchDelayInput.value}&buttonDelay=${buttonDelayInput.value}`)
        .then(response => response.json())
        .then(data => {
            updateSettings(data);
            console.log('Налаштування оновлені: ' + data);
            saveSettingsButton.disabled = true;
        })
        .catch(error => showError(error));
}

function afterLoad() {
    graph = document.getElementById('graph');
    powerButton = document.getElementById('powerButton');
    turboSwitch = document.getElementById('turboSwitch');
    humidityTriggerSwitch = document.getElementById('humidityTriggerSwitch');
    showSettingsButton = document.getElementById('showSettings');
    saveSettingsButton = document.getElementById('saveSettingsButton');
    ventHumidityLevelInput = document.getElementById('ventHumidityLevel');
    turboHumidityLevelInput = document.getElementById('turboHumidityLevel');
    offHumidityLevelInput = document.getElementById('offHumidityLevel');
    switchDelayInput = document.getElementById('switchDelay');
    buttonDelayInput = document.getElementById('buttonDelay');
    slider = document.getElementById("myRange");

    powerButton.addEventListener('click', () => {
        const state = powerButton.classList.contains('off') ? 1 : 0;
        sendState(state);
    });

    turboSwitch.addEventListener('change', () => {
        const state = turboSwitch.checked ? 2 : 1;
        sendState(state);
    });

    humidityTriggerSwitch.addEventListener('change',() => {
        const state = humidityTriggerSwitch.checked ? 1 : 0;
        sendHumidityTriggerState(state);
    });

    showSettingsButton.addEventListener('click', () => {
        let settingsClosed = showSettingsButton.classList.contains('fa-chevron-down');
        let settingsContainer = document.getElementById('settingsHideContainer');

        if (settingsClosed) {
            showSettingsButton.classList.remove('fa-chevron-down');
            showSettingsButton.classList.add('fa-chevron-up');
            settingsContainer.style.display = 'block';
        } else {
            showSettingsButton.classList.remove('fa-chevron-up');
            showSettingsButton.classList.add('fa-chevron-down');
            settingsContainer.style.display = 'none';
        }
    });

    ventHumidityLevelInput.addEventListener('change', () => changeSettings());
    turboHumidityLevelInput.addEventListener('change', () => changeSettings());
    offHumidityLevelInput.addEventListener('change', () => changeSettings());
    switchDelayInput.addEventListener('change', () => changeSettings());
    buttonDelayInput.addEventListener('change', () => changeSettings());

    saveSettingsButton.addEventListener('click', () => sendSettings())
    slider.addEventListener('change', () => {
        const sliderRange = slider.value;
        const dataIndex = 600 - sliderRange * 60;
        divider = sliderRange;

        chart.data.labels = chartData.labels.slice(dataIndex, 600);
        chart.data.datasets[0].data = chartData.datasets[0].data.slice(dataIndex, 600);
        chart.data.datasets[1].data = chartData.datasets[1].data.slice(dataIndex, 600);
        chart.data.datasets[2].data = chartData.datasets[2].data.slice(dataIndex, 600);

        chart.update();
    })

    updateData();
    getGraph();
    getSettings();

    setInterval(updateData, 5 * 1000);
    // setInterval(getGraph, 30 * 1000);
}
function changeSettings() {
    saveSettingsButton.disabled = false;
}
function switchVent(state) {
    switch (state) {
        case 0:
            powerButton.classList.add('off');
            powerButton.classList.remove('on');
            powerButton.classList.remove('turbo');
            break;
        case 1:
            powerButton.classList.remove('off');
            powerButton.classList.add('on');
            powerButton.classList.remove('turbo');
            break;
        case 2:
            powerButton.classList.remove('off');
            powerButton.classList.remove('on');
            powerButton.classList.add('turbo');
            break;
    }

}

function closeTimerModal() {
    const modal = document.getElementById('timerModal');
    modal.style.display = 'none';
}

function showTimerModal() {
    const modal = document.getElementById('timerModal');
    modal.style.display = 'block';
}

function showError(error) {
    console.error(error);

    const infoContainer = document.getElementById("info-container");
    const infoData = document.getElementById("info-data");
    infoData.textContent = 'Помилка: ' + error.message;
    infoContainer.classList.add('red');
    infoContainer.style.display = 'flex';

    const closeBtn = document.getElementById('close-error');
    closeBtn.style.display = 'block';

    closeBtn.onclick = function () {
        infoContainer.classList.remove('red');
        closeBtn.style.display = 'none';
        infoContainer.style.display = 'none';
    }
}

function zeroToNaN(number) {
    if (number <= 0) {
        return NaN;
    }
    return number;
}

function getLabel(index) {
    const date = new Date();
    let hours = new Date().getHours();
    const res = hours - (divider - index / 60);
    if (res == 0) {
        return `00:${date.getMinutes()}`;
    }
    if (res < 0) {
        return `${24 + res}:${date.getMinutes()}`;
    }

    return `${res}:${date.getMinutes()}`;
}

function updateGraph(graphData) {
    const labels = Array(graphData.length).fill('');
    divider = (graphData.length / 60) | 0;
    chart = new Chart(graph, {
        type: 'line',
        data: {
            labels: labels,
            datasets: [{
                label: 'Вологість',
                data: graphData.map(it => it.h).map(zeroToNaN),
                borderColor: 'rgb(90,205,255)',
                segment: {
                    borderColor: ctx => skipped(ctx, 'rgb(0,0,0,0.2)') || down(ctx, 'rgb(90,205,255)'),
                    borderDash: ctx => skipped(ctx, [6, 6]),
                },
                borderWidth: 2,
                spanGaps: true
            },{
                label: 'Температура',
                data: graphData.map(it => it.t).map(zeroToNaN),
                borderColor: 'rgb(255,132,42)',
                segment: {
                    borderColor: ctx => skipped(ctx, 'rgb(0,0,0,0.2)') || down(ctx, 'rgb(255,132,42)'),
                    borderDash: ctx => skipped(ctx, [6, 6]),
                },
                borderWidth: 2,
                spanGaps: true
            },{
                label: 'Режим',
                data: graphData.map(it => it.s).map(n => n === 0 ? 0 : 30 + n * 5).map(zeroToNaN),
                borderColor: 'rgb(29,157,0)',
                borderWidth: 2
            }]
        },
        options: {
            fill: false,
            interaction: {
                intersect: true
            },
            scales: {
                x: {
                    grid: {
                        color: function (context) {
                            if (context.index % 60 === 0) {
                                context.tick.label = getLabel(context.index);
                                return 'rgba(0,0,0,0.15)';
                            }

                            return "";
                        }
                    }
                }
            },
            radius: 0,
        }
    });
    chartData = JSON.parse(JSON.stringify(chart.data));
}

function updateSettings(settings) {
    ventHumidityLevelInput.value = settings.ventHumidityLevel;
    turboHumidityLevelInput.value = settings.turboHumidityLevel;
    offHumidityLevelInput.value = settings.offHumidityLevel;
    switchDelayInput.value = settings.switchDelay;
    buttonDelayInput.value = settings.buttonDelay;
}

function updateState(state) {
    switchVent(state.state);
    const infoContainer = document.getElementById("info-container");
    infoContainer.classList.remove('red');
    infoContainer.style.display = 'none';

    const infoData = document.getElementById("info-data");
    if (state.hoursOff != -1 && state.minutesOff != -1) {
        const timeString = String(state.hoursOff).padStart(2, '0') + ':' + String(state.minutesOff).padStart(2, '0');
        infoData.textContent = 'Вимкнеться о ' + timeString;
        infoContainer.style.display = 'flex';
    }

    if (state.humidityAlarm == 1) {
        infoData.textContent = 'Вимкнеться після зниження вологості!';
        infoContainer.style.display = 'flex';
    }

    const humidityElement = document.getElementById('humidity');
    const temperatureElement = document.getElementById('temperature');

    humidityElement.textContent = state.humidity + '%';
    temperatureElement.textContent = state.temperature + '°C';

    turboSwitch.checked = state.state == 2;
    humidityTriggerSwitch.checked = state.humidityTriggerAllowed == 1;
}

function parseState(text) {
    return JSON.parse(text.replace('nan', '0'));
}

function sendState(state) {
    fetch('/state?state=' + state)
        .then(response => response.text())
        .then(responseText => parseState(responseText))
        .then(data => {
            updateState(data);
            console.log('Стан оновлено: ' + data.state);
        })
        .catch(error => showError(error));
}
function sendHumidityTriggerState(state) {
    fetch('/trigger?state=' + state)
        .then(response => response.text())
        .then(responseText => parseState(responseText))
        .then(data => {
            updateState(data);
            console.log('Триггер оновлено: ' + data.humidityTriggerAllowed);
        })
        .catch(error => showError(error));
}

function getGraph() {
    fetch('/graph')
        .then(response => response.json())
        .then(data => updateGraph(data))
        // .catch(error => updateGraph(DDDATA));
        .catch(error => showError(error));
}

function updateData() {
    fetch('/getState')
        .then(response => response.text())
        .then(responseText => parseState(responseText))
        .then(data => updateState(data))
        .catch(error => showError(error));
}

function startTimerFromInput() {
    let input = document.getElementById('manualInput');
    let minutes = parseInt(input.value);
    if (isNaN(minutes) || minutes <= 0) {
        alert("Будь ласка, введіть правильний час в хвилинах.");
        return;
    }

    startTimer(minutes);
}

function startTimer(minutes) {
    fetch('/timer?minutes=' + minutes)
        .then(response => response.text())
        .then(responseText => parseState(responseText))
        .then(data => {
            closeTimerModal();
            updateState(data);
        })
        .catch(error => {
            closeTimerModal();
            showError(error);
        });
}